``` java
import java.awt.Canvas;
import java.awt.Color;
import java.awt.Frame;
import java.awt.GraphicsEnvironment;
import java.awt.GraphicsDevice;
import java.awt.Window;

class FsWindowApplication {
        private static class FsWindow extends Window {
                private static GraphicsDevice getScreenDevice() {
                        final GraphicsEnvironment env = GraphicsEnvironment.getLocalGraphicsEnvironment();
                        return env.getDefaultScreenDevice();
                }

                public FsWindow() {
                        super(new Frame(getScreenDevice().getDefaultConfiguration()));
                        final Canvas canvas = new Canvas();

                        canvas.setBackground(Color.RED);
                        add(canvas);
                        setVisible(true);
                }

                public void enterFullscreen() {
                        getScreenDevice().setFullScreenWindow(this);
                        validate();
                }

                public void exitFullscreen() {
                        setVisible(false);
                }
