        public static void main(String argv[]) {
                try {
                        final FsWindow fsWindow = new FsWindow();

                        Thread.currentThread().sleep(3000);
                        fsWindow.enterFullscreen();
                        Thread.currentThread().sleep(3000);
                        fsWindow.exitFullscreen();
